import React, { Component } from 'react'
import ApiService from '../Service/ApiService'
import {NavLink} from 'react-router-dom'


export class NormalUser extends Component {
    constructor(props) {
        super(props)
                var userdata =JSON.parse(sessionStorage.getItem('userloginmail'))
                var userLoggedIn =JSON.parse(sessionStorage.getItem('usertoken'))
        this.state = {
             taskname:'',
             taskdescription:'',
             status:'new',
             email:userdata.email,
             isuserlogin:userLoggedIn,
             userTaskList:[]

        }
    }
    componentDidMount()
    {

    }

    changeHandler=(e)=>
    {
        this.setState({[e.target.name]:e.target.value})
    }

    loadTask=()=>
    {
        var useremail = JSON.parse(sessionStorage.getItem('userloginmail'))
        console.log(useremail.email)
        var email = useremail.email
        ApiService.fetchTask(email).then(res=>{
            if(res.status===200)
            {
                    this.setState({userTaskList:res.data.result})
                    console.log(this.state.userTaskList)
                    console.log(res.data.result)
            }
        }).catch(error=>console.log(error))
    }

    submitTask=(e)=>
    {
        e.preventDefault();
        const TaskDetail={
            taskname :this.state.taskname,
            taskdescription : this.state.taskdescription,
            taskstatus:this.state.status,
            email:this.state.email
        }

        ApiService.addTask(TaskDetail).then(res=>{
           if(res.status === 200)
           {
               alert("Task added successfully...")
               console.log(res.data.result)// result = task insert successfully
               this.loadTask()

           }
        }).catch(error=>console.log(error))


    }




    render() {


        var userdata =JSON.parse(sessionStorage.getItem('userloginmail'))
            var name = userdata.fname.concat(" "+userdata.lname)



        return (

            <div style={{marginLeft :'300px'}}>

            <div style={{textAlign:'center',color:'red',fontStyle:'italic',marginRight:'300px',}}>
            <h4>Welcome <br/>{name} (user)</h4> </div>
            <div style={{marginTop:'40px'}}>
                    <form onSubmit={this.submitTask}>
                       <table>
                           <tr>
                            <th>TaskName</th>
                            <th>TaskDescription</th>
                           </tr>
                           <tr>
                        <td><textarea rows="2" cols="20" value={this.state.taskname} onChange={this.changeHandler} name="taskname" required></textarea></td>

                        <td><textarea rows="2" cols="20" value={this.state.taskdescription} onChange={this.changeHandler} name="taskdescription" required></textarea></td>
                        <td><button onClick ={this.submitTask} className="btn btn-primary">Add</button></td>
                        </tr>
                        </table> 
                        </form> 
            </div>


            <div style={{marginTop:'30px'}}>
                    <table style={{border:'1px solid black',borderCollapse:'separate'}}>
                    <tbody>
                        <tr>

                            <th></th>
                            <th></th>
                            <th>TaskName</th>
                            <th>TaskDescription</th>

                            <th>Status</th>
                            <th></th>
                            <th colSpan="2">Action</th>

                        </tr>
                        </tbody>
                        {/* <button>Complete</button> */}
                        <tr>
                            <td><input type="checkbox"/></td>
                            <td><input type="hidden"/></td>
                            <td></td>
                            <td></td>
                            <td><input type="text" value={this.state.status} style={{width:'80px',border:'none',backgroundColor:'grey'}}/></td>

                            <td><button className="btn btn-success">Edit</button></td>
                            <td><button className="btn btn-danger">Delete</button></td>

                        </tr>
                    </table>   

                    </div>       


            <div><NavLink to="/logout" className="btn btn-danger" style={{marginRight:'950px'}}>Logout</NavLink></div>
            </div>
        )
    }
}

export default NormalUser
