import React, { Component } from 'react'

import {Bar} from "react-chartjs-2";

export class Report extends Component {
    constructor(props) {
        super(props)

        this.state = {
             chartData:{
                 labels:['ActiveUsers','DeactiveUser','Task'],
                 datasets:[
                     {
                         label:'In Last Seven Days',
                         data:['mon',
                               'Tue',
                               'Wed',
                               'Thu',
                               'Fri',
                               'Sat',
                               'Sun'
                            ],
                            backgroundColor:[
                                'rgba(255, 99, 132, 0.2)',
                                'rgba(54, 162, 235, 0.2)',
                                'rgba(255, 206, 86, 0.2)',
                                'rgba(75, 192, 192, 0.2)',
                                'rgba(153, 102, 255, 0.2)',
                                'rgba(255, 159, 64, 0.2)'
                            ]

                     }
                 ]
             }
        }
    }

    render() {
        return (
            <div className="chart">
                <Bar data={this.state.chartData} width={100} height={50} options={{

                        maintainAspectRatio:false
                }}/>

            </div>
        )
    }
}

export default Report
