import React, { Component } from "react";


import { BrowserRouter, Route } from "react-router-dom";
import ApiService from "../Service/ApiService";

export class SignUp extends Component {
  constructor(props) {
    super(props);

    this.state = {
        allUserDetails:[], 
      FirstName: "",
      LastName:"",
      Email: "",
      Password: "",
      ConfirmPassword: "",
      firstnameError: "",
      lastnameError: "",
      emailError: "",
      passwordError: "",

    };
  }

  validForm = () => {
    var isValid = true;
    if (this.state.FirstName.length === 0) {
      this.setState({ firstnameError: "Firstname Should Not Be Blank" });
      isValid = false;
    } else if (this.state.FirstName.length > 0) {
      this.setState({ firstnameError: "" });
      isValid = true;
    }

    if (this.state.LastName.length === 0) {
        this.setState({ lastnameError: "Lastname Should Not Be Blank" });
        isValid = false;
      } else if (this.state.LastName.length > 0) {
        this.setState({ lastnameError: "" });
        isValid = true;
      }



    if (!this.state.Email.match(/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$/)) {
      this.setState({ emailError: "Email should contain . and @" });
      isValid = false;
    } else if (
      this.state.Email.match(/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$/)
    ) {
      this.setState({ emailError: "" });
      isValid = true;
    }



    if (!this.state.Password.match(/(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}/)) {
      this.setState({
        passwordError: "password should conatin atleast 1 capital 1 special character and minimum length of 8"
      });
      isValid = false;
    } else if (
      this.state.Password.match(/(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}/)
    ) {
      this.setState({ passwordError: "" });
      isValid = true;
    }
    if (this.state.Password != this.state.ConfirmPassword) {
      this.setState({
        passwordError: "Password And Confirm Password Not Match",
      });
      isValid = false;
    } else if (this.state.Password != this.state.ConfirmPassword) {
      this.setState({ passwordError: "" });
      isValid = true;
    }
    return isValid;
  };

  onchange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };


  submitData = (e) => {
    const validForm = this.validForm();
    ApiService.fetchUsers()
            .then((res) => {
                console.log(res.data)

                this.setState({allUserDetails : res.data.result})  // in map key is result
            }).catch((error)=>console.log(error));

    console.log(this.state.allUserDetails)

    var a = true;
    if (this.state.allUserDetails != null) {
      allUserDetails.map((user) => {
        if (user.Email === this.state.Email) {
          a = false;
        }
      });
    }
    if (this.state.allUserDetails == null) {
      this.state.allUserDetails = [];

      if (validForm == true) {
        const userDetails = {
          isAdmin: false,
          FirstName: this.state.FirstName,
          Email: this.state.Email,
          Password: this.state.Password,
          ConfirmPassword: this.state.ConfirmPassword,
        };
        this.state.allUserDetails.push(userDetails);
        localStorage.setItem("UsersData", JSON.stringify(allUserDetails));

        alert("data added to localStorage");
        this.props.history.push("/login");
      }
    } else if (allUserDetails != null) {
      if (validForm == true) {
        if (a == true) {
          const userDetails = {
            isAdmin: false,
            FirstName: this.state.FirstName,
            Email: this.state.Email,
            Password: this.state.Password,
            ConfirmPassword: this.state.ConfirmPassword,
          };
          allUserDetails.push(userDetails);
          localStorage.setItem("UsersData", JSON.stringify(allUserDetails));

          alert("data added to localStorage");
          this.props.history.push("/login");
        } else {
          alert(
            "your Email is already register...please sign up with another Email"
          );
        }
      } else {
        alert("something is wrong ");
      }
    }
  };

  render() {
    return (
      <div className="box" style={{marginLeft:'400px',marginTop:'20px'}}>
        <form autoComplete="off">
          <div className="col-md-5 form-group" style={{textAlign:'center'}}>

            <input
              type="text"
              name="FirstName"
              value={this.state.FirstName}
              onChange={this.onchange}
              placeholder="First Name"
              required
              className="form-control"
            />
            <pre style={{ color: "red" }}> {this.state.firstnameError}</pre>
          </div>


          <div className="col-md-5 form-group">

            <input
              type="text"
              name="LastName"
              value={this.state.LastName}
              onChange={this.onchange}
              placeholder="Last Name"
              required
              className="form-control"
            />
            <pre style={{ color: "red" }}> {this.state.lastnameError}</pre>
          </div>

          <div className="col-md-5 form-group">

            <input
              type="text"
              name="Email"
              value={this.state.Email}
              onChange={this.onchange}
              placeholder="Email"
              required
              className="form-control"
            />
            <pre style={{ color: "red" }}> {this.state.emailError}</pre>
          </div>



          <div className="col-md-5 form-group">

            <input
              type="password"
              name="Password"
              value={this.state.Password}
              onChange={this.onchange}
              placeholder="Password"
              required
              className="form-control"
            />
            <div style={{ color: "red" }} > {this.state.passwordError}</div>
          </div>

          <div className="col-md-5 form-group">

            <input
              type="password"
              name="ConfirmPassword"
              value={this.state.ConfirmPassword}
              onChange={this.onchange}
              placeholder="ConfirmPassword"
              required
              className="form-control"
            />
          </div>

          <button
            type="button"
            className="btn btn-primary m-2"
            onClick={this.submitData}
          >
            {" "}
            Sign up{" "}
          </button>
        </form>
      </div>
    );
  }
}

export default SignUp;
