import React, { Component } from 'react'

import {Bar} from "react-chartjs-2";
import ApiService from '../Service/ApiService';
import { Redirect } from 'react-router-dom';

export class Report extends Component {
    constructor(props) {
        super(props)
        let admintoken = sessionStorage.getItem("admintoken")

        this.state = {
            reports:{},
            adminlog: admintoken,
            Data:{
                labels:['Activate Users','Deactive User','Total Task'],
            }
        }
    }
    componentDidMount() {
        if(this.state.adminlog){
        let allreport = []
        ApiService.Report()
            .then(res => {
                if (res.status === 200) {
                    allreport = res.data
                }
             let chartData =
             {
                 labels:['Activate Users','Deactive User','Total Task'],
                 datasets:[
                     {
                         label:'Total Activity in Last 7 Days',
                         data:[10, //count of active users
                               20, //count of deactivate user

                               30 //count of total task
                            ],//allreport
                            backgroundColor:[
                                'rgba(255, 99, 132, 0.2)',
                                'rgba(54, 162, 235, 0.2)',
                                'rgba(255, 206, 86, 0.2)',

                            ],

                     }
                 ], 
            }
            this.setState({reports:allreport})
        }).catch(error=>console.log(error))

        }
    }

    option = {
        title: {
            display: true,
            text: 'Report'
        },
        scales: {
            yAxes: [
                {
                    ticks: {
                        beginAtZero:true,
                        stepSize: 1
                    }
                }
            ]
        }
    }


    render() {
        if(this.state.adminlog){
        return (
            <div className="chart">
                <Bar data={this.state.reports} options={this.option}/>

            </div>
        )
    }
    else{
        return <Redirect to='/'/>
    }
}
}

export default Report
