import React, { Component } from 'react'

import {Bar} from "react-chartjs-2";
import ApiService from '../Service/ApiService';
import { Redirect } from 'react-router-dom';

export class Report extends Component {
    constructor(props) {
        super(props)



    }

componentDidMount()
{
    let admintoken = sessionStorage.getItem("admintoken")
    this.state = {
        reports:{},
        count:[],
        adminlog:admintoken,

        Data:{
            labels:['New Users','Activated User','Total Task'],
            datasets:[
                {
                    label:'All activity in last 7 days',
                    data:[20,18,40],//this.state.count
                    backgroundColor:[
                        'rgba(75, 192, 192, 1)',
                        'rgba(153, 102, 255, 1)',
                        'rgba(255, 159, 64, 1)'
                    ],
                }]

}
}

}



    option = {
        title: {
            display: true,
            text: 'Report'
        },
        scales: {
            yAxes: [
                {
                    ticks: {
                        //beginAt:10,
                        beginAtZero:true,
                        stepSize:10

                    }
                }
            ]
        }
    }


    render() {
        if(this.state.adminlog){
        return (
            <div className="chart">
                <Bar data={this.state.Data} options={this.option}/>

            </div>
        )
    }
    else{
        return <Redirect to='/'/>
    }
}
}

export default Report
