import React, { Component } from "react";
//import "./Styles/LogIn.css";
import { Link, BrowserRouter, Route, NavLink, Switch } from "react-router-dom";
import SignUp from "./SignUp";

export class LogIn extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      password: "",
      allUsersDetails: [],
      error: {
        usernameerror: "",
        passworderror: "",
      },
    };
  }

  verifyUser = () => {
    const username = this.state.username;
    const password = this.state.password;
    const userdetail = this.state.allUsersDetails;

    var flag = 0;
    var invaliduser = 0;
    var name = "";
    for (var user of userdetail) {
      if (
        username === user.Email &&
        password === user.Password &&
        user.isAdmin === false
      ) {
        flag = 1;
        name = user.FirstName;
        break;
      }

      if (
        username === user.Email &&
        password === user.Password &&
        user.isAdmin === true
      ) {
        flag = 2;
        name = user.FirstName;
        break;
      }
    }

    if (flag === 1) {
      this.props.history.push(`/home/${name}`);
    }
    if (flag === 2) {
      this.props.history.push(`/admin/${name}`);
    }

    if (flag == 0) {
      alert("login failed");
    }

    this.setState({
      username: "",
      password: "",
    });
  };
//   componentDidMount = () => {
//     this.setState({
//       allUsersDetails: JSON.parse(localStorage.getItem("UsersData")),
//     });
//   };
  validLoginForm = (e) => {
    if (this.state.username.length == 0) {
      this.setState({
        error: { usernameerror: "uername should not be empty" },
      });
      // alert(this.state.error.passworderror)
    } else if (this.state.password.length == 0) {
      this.setState({
        error: { passworderror: "password should not be empty" },
      });
      // alert(this.state.error.passworderror)
    }

    this.verifyUser();
  };

  OnChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  render() {
    return (
        <BrowserRouter>
      <div style={{marginLeft:'300px',marginTop:'-100px'}}> 
        <form style={{ maxWidth: "500px", margin: "auto", marginTop: "150px" }}
        >
           <div className="col-md-8 form-group" style={{textAlign:'center'}}>
            <i className="fa fa-user icon"></i>
            <input
              className="input-field"
              type="text"
              placeholder="Email"
              autoComplete="off"
              name="username"
              value={this.state.username}
              onChange={this.OnChange}
            />
          </div>
          <pre style={{ color: "red" }}>{this.state.error.usernameerror}</pre>


          <span className="input-container">
            <i className="fa fa-key icon"></i>
            <input
              className="input-field"
              type="password"
              placeholder="Password"
              autoComplete="off"
              value={this.state.password}
              name="password"
              onChange={this.OnChange}
            />
          </span>
          <pre style={{ color: "red" }}>{this.state.error.passworderror}</pre>

          <button
            type="button"
            className="btn btn-primary"
            value={this.state.username}
            onClick={this.validLoginForm}
          >
            LogIn
          </button> &nbsp;


          <div>
            <b>need an account? click on SignUp </b>{" "}

          </div>
        </form>
      </div>

      <Switch>
        {/* <Route path="/signup" component={}/> */}
      </Switch>
      </BrowserRouter>

    );
  }
}

export default LogIn;
