  OnChange=(e)=>
  {
      this.setState({
        [e.target.name] : e.target.value
      })
    this.verifyUser();
  };


  OnChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };



    render() {
        return (
            <div>
                <form a style={{maxWidth:'500px',margin:'auto',marginTop:'150px'}}>

                <span className="input-container">
