import React, { Component } from "react";
//import "./Styles/LogIn.css";
import {BrowserRouter, Switch } from "react-router-dom";
import SignUp from "./SignUp";

export class LogIn extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      password: "",
      allUsersDetails: [],
      error: {
        usernameerror: "",
        passworderror: "",
      },
    };
  }

  verifyUser = () => {
    const username = this.state.username;
    const password = this.state.password;
    const userdetail = this.state.allUsersDetails;

    var flag = 0;
    var invaliduser = 0;
    var name = "";
    for (var user of userdetail) {
      if (
        username === user.Email &&
        password === user.Password &&
        user.isAdmin === false
      ) {
        flag = 1;
        name = user.FirstName;
        break;
      }

      if (
        username === user.Email &&
        password === user.Password &&
        user.isAdmin === true
      ) {
        flag = 2;
        name = user.FirstName;
        break;
      }
    }

    if (flag === 1) {
      this.props.history.push(`/home/${name}`);
    }
    if (flag === 2) {
      this.props.history.push(`/admin/${name}`);
    }

    if (flag == 0) {
      alert("login failed");
    }

    this.setState({
      username: "",
      password: "",
    });
  };
//   componentDidMount = () => {
//     this.setState({
//       allUsersDetails: JSON.parse(localStorage.getItem("UsersData")),
//     });
//   };

  validLoginForm = (e) => {
    if (this.state.username.length == 0) {
      this.setState({
        error: { usernameerror: "uername should not be empty" },
      });

    }else if (!this.state.username.match(/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$/)) {
      this.setState({ error:{ usernameerror:"Email should contain . and @" }});
      isValid = false;
    } else if (this.state.Email.match(/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$/)) 
    {
        this.setState({error:{usernameerror:""}});
    }
    else if (this.state.password.length == 0) {
      this.setState({error: { passworderror: "password should not be empty" },
      });

    }

    this.verifyUser();
  };

  OnChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  render() {
    return (
        <BrowserRouter>
      <div style={{marginLeft:'100px',marginTop:'-100px'}}> 
        <form style={{ maxWidth: "500px", margin: "auto", marginTop: "150px" }}
        >

          <div className="col-md-9 form-group" >

            <input
              type="text"
              name="username"
              value={this.state.username}
              onChange={this.OnChange}
              placeholder="Email"
              required
              className="form-control"
            />

          </div>

          <pre style={{ color: "red" ,marginLeft:'30px'}}>{this.state.error.usernameerror}</pre>

          <div className="col-md-9 form-group" style={{textAlign:'center'}}>

            <input
              type="text"
              name="password"
              value={this.state.password}
              onChange={this.OnChange}
              placeholder="Password"
              required
              className="form-control"
            />

          </div>
          <pre style={{ color: "red",marginLeft:'30px'}}>{this.state.error.passworderror}</pre>


          <div className="col-md-9 form-group" style={{textAlign:'center'}}>
          <button  className="btn btn-primary"
            value={this.state.username}
            onClick={this.validLoginForm}
          >LogIn
          </button>
          </div>

          <div style={{marginLeft:'60px'}}>
            <b>need an account? click on SignUp </b>

          </div>
        </form>
      </div>

      <Switch>
        {/* <Route path="/signup" component={}/> */}
      </Switch>
      </BrowserRouter>

    );
  }
}

export default LogIn;
