import React, { Component } from 'react'
import ApiService from '../Service/ApiService'


class Admin extends Component {

    constructor(props) {
        super(props)
        this.state = {
            users: [],
            message: null
        }

    }

    componentDidMount() {
        this.UserList();
    }

    UserList=()=> {
        ApiService.fetchUsers()
            .then((res) => {
                console.log(res.data)


                this.setState({users: res.data.result}) // in map key is result
            });
    }

    ActivateUser=(userid,val)=>
    {
        const data={
            userid : userid,
            activate:val
        }

        ApiService.updateUser(data).then(res=>{
            if(res)
            {
                console.log(res.data)
                alert("status changed to activate")
            }}).catch(error=>
                alert(error));
    }

    DectivateUser=(userid,val)=>
    {
        const data={
            userid : userid,
            activate:val
        }

      console.log(data)

        ApiService.updateUser(data).then((res)=>
        console.log(res.data)).catch((error)=>
        {
            console.log(error)
        })

    }
    handleSubmit()
    {

    }

    render() {
        console.log(this.state.users.map(u=>u.id))
        return (
            <div>
                <h2 className="text-center">User Details</h2>
                <form action='http://localhost:8080/WishTreeProject/UpdateUserServlet' type="post" onSubmit={this.handleSubmit()}>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            {/* <th className="hidden">Id</th>
                            <th>Id</th> */}
                            <th>Name</th>
                            <th>Address</th>
                            <th>Gender</th>
                            <th colSpan="2">Action</th>

                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.users.map( user =>
                                    <tr key={user.id}>

                                        <td>{user.name}</td>
                                        <td>{user.address}</td>
                                        <td>{user.gender}</td>

                                        <td><button  className="btn btn-success" onClick={()=>this.ActivateUser(user.id,true)}>Activate</button></td>
                                        <td><button className="btn btn-danger" onClick={()=>this.DectivateUser(user.id,false)}>Deactivate</button></td>

                                        <td>

                                        </td>
                                    </tr>
                            )


                        }
                    </tbody>

                </table>
                </form>

            </div>
        );
    }

}

export default Admin;
