import React, { Component } from 'react'

export class VerifyUser extends Component {
    constructor(props) {
        super(props)

        this.state = {
             code:0
        }
    }
    HandleChange=(e)=>
    {
        this.setState({[e.target.name]:e.target.value})
    }

    render() {
        return (
            <div style={{textAlign:'center'}}>
                <form>
                    <h3>verification code alreday send to your email.</h3>

                   <label>Enetr Code</label> <input type="text" name="code" value={this.state.code} onChange={this.HandleChange} placeholder="enter verification code here"/>
                </form>
            </div>
        )
    }
}

export default VerifyUser
